import Card from '../../UI/Card';
import Input from '../../UI/Input';
import Button from '../../UI/Button';
import { Link } from 'react-router-dom';

import styles from './Login.module.css';

const Login = () => {
  const submitHandler = (event) => {
    event.preventDefault();
    console.log('Successfully called!');
  };

  const emailValidation = (value) => {
    if (!value.includes('@')) {
      return { isValid: false, errorMessage: 'Email adress must inlcude "@"!' };
    }
  };

  const passwordValidation = (value) => {
    if(value.length < 7){
			return {isValid: false, errorMessage: 'Password must be at least 7 characters long!'}
		}
  };

  return (
    <form onSubmit={submitHandler}>
      <Card additionalStyles={styles.loginCard} center={true}>
        <Input type='email' label='Email: ' validate={emailValidation}></Input>
        <Input
          type='text'
          label='Password: '
          validate={passwordValidation}
        ></Input>
        <Button additionalStyles={styles.loginButton} buttonType='submit'>
          Login
        </Button>
        <Link className={styles.registerNow} to='/register'>
          Don't have an account? <br />
          Register now!
        </Link>
      </Card>
    </form>
  );
};

export default Login;
