import { useContext, useEffect, useState, useCallback, useRef } from 'react';
import swal from 'sweetalert';

import AuthContext from '../auth/store/auth-context';
import useHttp from '../hooks/use-http';
import Card from '../UI/Card';
import KeyValueTextBox from '../UI/KeyValueTextBox';
import Button from '../UI/Button';

import styles from './Profile.module.css';
import Input from '../UI/Input';

const Profile = () => {
  const nameRef = useRef('');
  const surnameRef = useRef('');
  const authCtx = useContext(AuthContext);
  const [user, setUser] = useState({});
  const http = useHttp();

  const fetchSuccess = (data) => {
    setUser(data);
    nameRef.current.refValue.current.value = data.name;
    surnameRef.current.refValue.current.value = data.surname;
  };

  const fetchFail = (errorMessage) => {
    swal('Oops', errorMessage || 'Failed to fetch profile info', 'warning');
  };

  const { sendRequest } = http;
  const { token } = authCtx;
  const fetchProfile = useCallback(() => {
    let requestConfig = {
      url: 'http://localhost:5000/api/person/get-person-by-email',
      headers: { Authorization: `Bearer ${token}` },
      method: 'GET',
    };
    sendRequest(requestConfig, fetchSuccess, fetchFail);
  }, [sendRequest, token]);

  useEffect(() => {
    fetchProfile();
  }, [fetchProfile]);

	const changeSuccess = () =>{

	}

	const changeFail = () =>{
		
	}

  return (
    <Card additionalStyles={styles.container}>
      <div className={styles.profileInfo}>Profile info</div>
      <KeyValueTextBox name='Name: ' value={<Input ref={nameRef}></Input>} />
      <KeyValueTextBox
        name='Surname: '
        value={<Input ref={surnameRef}></Input>}
      />
      <KeyValueTextBox
        name='Email: '
        value={user.email}
        additionalStylesValue={styles.valueStyle}
      />
      <div className={styles.btnContainer}>
        <Button>Delete</Button>
        <Button>Change</Button>
      </div>
    </Card>
  );
};

export default Profile;
