import { useEffect, useState, useContext, useCallback, useRef } from 'react';
import { useHistory } from 'react-router-dom';

import swal from 'sweetalert';
import ClipLoader from 'react-spinners/ClipLoader';

import AuthContext from '../auth/store/auth-context';
import useHttp from '../hooks/use-http';
import Button from '../UI/Button';
import Card from '../UI/Card';
import KeyValueTextBox from '../UI/KeyValueTextBox.js';
import styles from './AllUsers.module.css';
import SearchBar from './SearchBar';
import UserAddFriend from './UserAddFriend';

const AllUsers = () => {
  const http = useHttp();

  const searchResults = (data) => {
    setResults(data.people);
  };

  return (
    <div className={styles.container}>
      {results.length !== 0 && (
        <SearchBar searchResultsHandler={searchResults} http={http}></SearchBar>
      )}
      <UserAddFriend searchResultsHandler={searchResults}/>
      {results.length === 0 && !http.isLoading && (
        <div style={{ marginTop: '100px' }}>
          <Card>No one left to make friends with :(</Card>
        </div>
      )}
    </div>
  );
};

export default AllUsers;
