import { useRef } from 'react';

import Card from '../../UI/Card';
import Input from '../../UI/Input';
import Button from '../../UI/Button';
import useHttp from '../../hooks/use-http';

import styles from './Register.module.css';

const Register = () => {
  const firstNameInput = useRef();
  const lastNameInput = useRef();
  const emailInput = useRef();
  const passwordInput = useRef();
  const { isLoading, sendRequest } = useHttp();

  const registerSuccess = () => {
    alert('Successfully registered a new user!');
  };

  const registerFail = (errorMessage) => {
    alert(errorMessage || 'Failed to register a new user!');
  };

  const submitHandler = (event) => {
    event.preventDefault();
    if (isLoading) {
      return;
    }

    let requestConfig = {
      url: 'https://localhost:3000/register',
      body: {
        Name: firstNameInput.current.value,
        Surname: lastNameInput.current.value,
        Email: emailInput.current.value,
        Password: passwordInput.current.value,
      },
      method: 'POST',
      headers: { 'Content-type': 'application/json' },
    };
    
    sendRequest(requestConfig, registerSuccess, registerFail);
  };

  return (
    <form onSubmit={submitHandler}>
      <Card additionalStyles={styles.registerCard}>
        <Input type='text' label='First name: ' ref={firstNameInput}></Input>
        <Input type='text' label='Last name: ' ref={lastNameInput}></Input>
        <Input type='text' label='Email: ' ref={emailInput}></Input>
        <Input type='text' label='Password: ' ref={passwordInput}></Input>
        <Button
          additionalStyles={`${styles.registerButton} ${
            isLoading && styles.registerLoading
          }`}
          buttonType='submit'
        >
          Register
        </Button>
      </Card>
    </form>
  );
};

export default Register;
