import { useEffect, useState, useContext } from 'react';
import swal from 'sweetalert';

import AuthContext from '../auth/store/auth-context';
import useHttp from '../hooks/use-http';
import Button from '../UI/Button';
import Card from '../UI/Card';
import KeyValueTextBox from '../UI/KeyValueTextBox.js';
import styles from './AllUsers.module.css';

const AllUsers = () => {
  const http = useHttp();
  const [results, setResults] = useState([]);
  const authCtx = useContext(AuthContext);

  const fetchSuccess = (data) => {
    setResults(data.people);
  };

  const fetchFail = (errorMessage) => {
    swal('FAILED', errorMessage || 'Failed to load all the users!', 'warning');
  };

  let { sendRequest } = http;
  let { token } = authCtx;

  useEffect(() => {
    let requestConfig = {
      url: 'http://localhost:5000/api/person/get-all-people',
      headers: { Authorization: `Bearer ${token}` },
      method: 'GET',
    };
    sendRequest(requestConfig, fetchSuccess, fetchFail);
  }, [sendRequest, token]);

  const addFriendHandler = (email) => {
    let requestConfig = {
      url: 'http://localhost:5000/api/person/add-friend?email',
      headers: { Authorization: `Bearer ${token}` },
      method: 'PUT',
    };
    sendRequest(requestConfig, fetchSuccess, fetchFail);
  };

  return (
    <div className={styles.container}>
      {results.map((element, index) => (
        <Card additionalStyles={styles.cardStyle} key={index}>
          <KeyValueTextBox
            name={'Name: '}
            value={element.name}
          ></KeyValueTextBox>
          <KeyValueTextBox
            name={'Surname: '}
            value={element.surname}
          ></KeyValueTextBox>
          <KeyValueTextBox
            name={'Email: '}
            value={element.email}
          ></KeyValueTextBox>
          <Button
            buttonHandler={addFriendHandler.arguments(element.email)}
            additionalStyles={styles.btnStyle}
          >
            Add friend
          </Button>
        </Card>
      ))}
    </div>
  );
};

export default AllUsers;
