import { useRef } from 'react';

import Card from '../../UI/Card';
import Input from '../../UI/Input';
import Button from '../../UI/Button';
import useHttp from '../../hooks/use-http';

import styles from './Register.module.css';

const Register = () => {
  const firstName = useRef();
  const lastName  = useRef();
  const email = useRef();
  const password = useRef();
  const { isLoading, sendRequest } = useHttp();
  
  let requestConfig = {}

  const registerSuccess = () => {
    alert('Successfully registered a new user!');
  };

  const registerFail = () => {
    alert('Failed to register a new user!');
  };

  const submitHandler = (event) => {
    event.preventDefault();
    sendRequest(requestConfig, registerSuccess, registerFail)
  };

  return (
    <form onSubmit={submitHandler}>
      <Card additionalStyles={styles.registerCard}>
        <Input type='text' label='First name: ' ref={firstName}></Input>
        <Input type='text' label='Last name: '></Input>
        <Input type='text' label='Email: '></Input>
        <Input type='text' label='Password: '></Input>
        <Button additionalStyles={styles.registerButton} buttonType='submit'>
          Register
        </Button>
      </Card>
    </form>
  );
};

export default Register;
