import { forwardRef, useImperativeHandle, useRef } from 'react';

import Input from '../UI/Input';
import Button from '../UI/Button';
import styles from './SearchBar.module.css';
import useHttp from '../hooks/use-http';

const SearchBar = forwardRef((props, ref) => {
  const searchRefName = useRef();
  const searchRefSurname = useRef();
  const http = useHttp();

  useImperativeHandle(ref, () => {
    return {
      refName: searchRefName.current.refValue,
      refSurname: searchRefSurname.current.refValue,
    };
  });

  const searchSuccess = (data) => {
    if (data.people.length === 0) {
      swal('Oops', 'No data was found!', 'warning');
      return;
    }
    setResults(data.people);
  };

  const searchFail = (errorMessage) => {
    swal('Oops', errorMessage || 'No data was found!', 'warning');
  };

  const searchHandler = () => {
    let name = searchRef.current.refName.current.value.trim().split(' ')[0];
    let surname = searchRef.current.refSurname.current.value.trim().split(' ')[0];

    let requestConfig = {
      url:
        'http://localhost:5000/api/person/search?' +
        `name=${encodeURI(name ?? '')}&surname=${encodeURI(surname ?? '')}`,
      headers: { Authorization: `Bearer ${token}` },
      method: 'GET',
    };
    sendRequest(requestConfig, searchSuccess, searchFail);
    searchRef.current.refName.current.value = '';
    searchRef.current.refSurname.current.value = '';
  };

  return (
    <div className={styles.searchDiv}>
      <Input
        ref={searchRefName}
        label={''}
        additionalStylesLabel={styles.searchLabel}
        additionalStylesInput={styles.searchInput}
        placeholder={'Name'}
      ></Input>{' '}
      <Input
        ref={searchRefSurname}
        label={''}
        additionalStylesLabel={styles.searchLabel}
        additionalStylesInput={styles.searchInput}
        placeholder={'Surname'}
      ></Input>
      <Button buttonHandler={props.searchHandler}>Search</Button>
    </div>
  );
});

export default SearchBar;
