import { useContext, useEffect, useState, useCallback, useRef } from 'react';
import swal from 'sweetalert';
import ClipLoader from 'react-spinners/ClipLoader';

import AuthContext from '../auth/store/auth-context';
import useHttp from '../hooks/use-http';
import Card from '../UI/Card';
import KeyValueTextBox from '../UI/KeyValueTextBox';
import Button from '../UI/Button';

import styles from './Profile.module.css';
import Input from '../UI/Input';
import ProfileInfo from './UserProfile/ProfileInfo';

const Profile = () => {
  const newPasswordRef = useRef();
  const oldPasswordRef = useRef();
  const [friends, setFriends] = useState([]);
  const http = useHttp();

  
  const { sendRequest } = http;
  const { token } = authCtx;

  const loadFriendsSuccess = (data) => {
    setFriends(data.friends);
  };

  const loadFriendsFail = () => {
    swal('Error', "Couldn't retrieve the friend list", 'error');
  };

  const loadFriendsHandler = useCallback(() => {
    let requestConfig = {
      url: 'http://localhost:5000/api/person/all-friends',
      headers: { Authorization: `Bearer ${token}` },
      method: 'GET',
    };
    sendRequest(requestConfig, loadFriendsSuccess, loadFriendsFail);
  }, [sendRequest, token]);

  const removeFriendSuccess = () => {
    swal('Success', 'Your friendship has ended!', 'success');
    loadFriendsHandler();
  };

  const removeFriendFail = () => {
    swal('Oops', 'Failed to remove friend!', 'warning');
  };

  const removeFriendHandler = (email) => {
    let requestConfig = {
      url: `http://localhost:5000/api/person/remove-friend?email=${encodeURI(
        email
      )}`,
      headers: { Authorization: `Bearer ${token}` },
      method: 'DELETE',
    };
    sendRequest(requestConfig, removeFriendSuccess, removeFriendFail);
  };

  useEffect(() => {
    loadFriendsHandler();
  }, [loadFriendsHandler]);

  const updatePasswordSuccess = () => {
    swal('Success', 'Password has been changed!', 'success');
  };

  const updatePasswordFail = (errorMessage) => {
    swal('Error', errorMessage || 'Unable to update the password!', 'error');
  };

  const updatePasswordHandler = (newPassword, oldPassword) => {
    let requestConfig = {
      url: 'http://localhost:5000/api/person/update-password',
      body: {
        OldPassword: oldPasswordRef.current.refValue.current.value,
        NewPassword: newPasswordRef.current.refValue.current.value,
      },
      headers: {
        Authorization: `Bearer ${token}`,
        'Content-type': 'application/json',
      },
      method: 'PUT',
    };
    sendRequest(requestConfig, updatePasswordSuccess, updatePasswordFail);
  };

  return (
    <div>
      {http.isLoading ? (
        <div style={{ marginTop: '200px', textAlign: 'center' }}>
          <ClipLoader loading={http.isLoading} color={'#ffffff'} size={150} />
        </div>
      ) : (
        <div className={styles.container}>
          <ProfileInfo />
          <Card>
            <div className={styles.profileInfo}>Change password</div>
            <Input
              additionalStylesDiv={styles.containerStyle}
              additionalStylesLabel={styles.userLabel}
              additionalStylesInput={styles.passwordInput}
              label={'New password: '}
              ref={newPasswordRef}
            ></Input>
            <Input
              additionalStylesDiv={styles.containerStyle}
              additionalStylesInput={styles.passwordInput}
              additionalStylesLabel={styles.userLabel}
              label={'Old password: '}
              ref={oldPasswordRef}
            ></Input>
            <div className={styles.btnContainer}>
              <Button buttonHandler={updatePasswordHandler}>Update</Button>
            </div>
          </Card>
          <div
            style={{
              marginTop: '10px',
              marginBottom: '0px',
              alignSelf: 'center',
            }}
            className={styles.profileInfo}
          >
            Friends
          </div>
          {!http.isLoading ? (
            friends.map((friend, index) => (
              <Card additionalStyles={styles.cardStyle} key={index}>
                <KeyValueTextBox name={'Name: '} value={friend.friend.name} />
                <KeyValueTextBox
                  name={'Surname: '}
                  value={friend.friend.surname}
                />
                <KeyValueTextBox name={'Email: '} value={friend.friend.email} />
                <KeyValueTextBox
                  name={'Type: '}
                  value={friend.type.replace('_', ' ')}
                />
                <Button
                  buttonHandler={() => {
                    return removeFriendHandler(friend.friend.email);
                  }}
                  additionalStyles={styles.btnStyle}
                >
                  Remove friend
                </Button>
              </Card>
            ))
          ) : (
            <div style={{ marginTop: '20px', alignSelf: 'center' }}>
              <ClipLoader
                loading={http.isLoading}
                color={'#ffffff'}
                size={150}
              />
            </div>
          )}
          {friends.length === 0 && !http.isLoading && (
            <div style={{ marginTop: '20px', alignSelf: 'center' }}>
              <Card>Ready to make some friends!</Card>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default Profile;
