import useInputValidation from '../hooks/validate-input';
import styles from './Input.module.css';

const Input = (props) => {
  const { hasError, onChangeHandler, onBlurHandler, errorMessage } =
    useInputValidation(props.validate);

  console.log(props.label + '  ' + hasError);

  return (
    <div>
      {hasError ? <div className={styles.error}>{errorMessage}</div> : ''}
      <div className={`${styles.container} ${props.additionalStylesDiv}`}>
        <label
          className={`${styles.label} ${props.additionalStylesLabel}`}
          htmlFor={props.id}
        >
          {props.label}
        </label>
        <input
          className={`${styles.field} ${props.additionalStylesInput} ${hasError ? styles.field-error : ''}`}
          id={props.id}
          type={props.type}
          onChange={onChangeHandler}
          onBlur={onBlurHandler}
        ></input>
      </div>
    </div>
  );
};

export default Input;
