import { useContext, useEffect, useState, useCallback, useRef } from 'react';
import swal from 'sweetalert';
import ClipLoader from 'react-spinners/ClipLoader';

import AuthContext from '../auth/store/auth-context';
import useHttp from '../hooks/use-http';
import Card from '../UI/Card';
import KeyValueTextBox from '../UI/KeyValueTextBox';
import Button from '../UI/Button';

import styles from './Profile.module.css';
import Input from '../UI/Input';

const Profile = () => {
  const nameRef = useRef();
  const surnameRef = useRef();
  const authCtx = useContext(AuthContext);
  const [user, setUser] = useState({ name: '', surname: '' });
  const [friends, setFriends] = useState([]);
  const http = useHttp();

  const fetchSuccess = (data) => {
    console.log(data);
    setUser(data);
  };

  const fetchFail = (errorMessage) => {
    swal('Oops', errorMessage || 'Failed to fetch profile info', 'warning');
  };

  const { sendRequest } = http;
  const { token } = authCtx;

  const fetchProfile = useCallback(() => {
    let requestConfig = {
      url: 'http://localhost:5000/api/person/get-person-by-email',
      headers: { Authorization: `Bearer ${token}` },
      method: 'GET',
    };
    sendRequest(requestConfig, fetchSuccess, fetchFail);
  }, [sendRequest, token]);

  const changeSuccess = () => {
    swal('SUCCESS', 'Successfully changed profile information!', 'success');
  };

  const changeFail = (message) => {
    swal('Oops', message || 'Failed to change profile info!', 'error');
  };

  const changeProfile = () => {
    let requestConfig = {
      url:
        'http://localhost:5000/api/person/update-person?name=' +
        `${encodeURI(nameRef.current.rawValue)}&surname=${encodeURI(
          surnameRef.current.rawValue
        )}`,
      headers: { Authorization: `Bearer ${token}` },
      method: 'PUT',
    };
    sendRequest(requestConfig, changeSuccess, changeFail);
  };

  const deleteSuccess = () => {
    swal('SUCCESS', 'Account deleted!', 'success').then(() => {
      authCtx.logout();
    });
  };

  const deleteFail = () => {
    swal('ERROR', "Couldn't delete the account!", 'error');
  };

  const onDeleteHandler = () => {
    let requestConfig = {
      url: 'http://localhost:5000/api/person/delete-account',
      headers: { Authorization: `Bearer ${token}` },
      method: 'DELETE',
    };
    sendRequest(requestConfig, deleteSuccess, deleteFail);
  };

  const removeFriendHandler = () => {
    setFriends([]);
  };

  const loadFriendsSuccess = (data) => {
		setFriends(data.people)
		console.log(data.people)
	};

  const loadFriendsFail = () => {
		swal('ERROR', 'Couldn\'t retrieve the friend list', 'error')
	};

  const loadFriendsHandler = useCallback(() => {
    let requestConfig = {
      url: 'http://localhost:5000/api/person/all-friends',
      headers: { Authorization: `Bearer ${token}` },
      method: 'DELETE',
    };
    sendRequest(requestConfig, loadFriendsSuccess, loadFriendsFail);
  }, [sendRequest, token]);
	
  useEffect(() => {
    fetchProfile();
		loadFriendsHandler();
  }, [fetchProfile, loadFriendsHandler]);

  return (
    <div className={styles.container}>
      {!http.isLoading ? (
        <Card>
          <div className={styles.profileInfo}>Profile info</div>
          <Input
            additionalStylesDiv={styles.containerStyle}
            additionalStylesLabel={styles.userLabel}
            additionalStylesInput={styles.userInput}
            label={'Name: '}
            ref={nameRef}
          >
            {user.name}
          </Input>
          <Input
            additionalStylesDiv={styles.containerStyle}
            additionalStylesInput={styles.userInput}
            additionalStylesLabel={styles.userLabel}
            label={'Surname: '}
            ref={surnameRef}
          >
            {user.surname}
          </Input>
          <KeyValueTextBox
            name='Email: '
            value={user.email}
            additionalStylesValue={styles.valueStyle}
          />
          <div className={styles.btnContainer}>
            <Button buttonHandler={onDeleteHandler}>Delete</Button>
            <Button buttonHandler={changeProfile}>Change</Button>
          </div>
        </Card>
      ) : (
        <div style={{ marginTop: '20px', alignSelf: 'center' }}>
          <ClipLoader loading={http.isLoading} color={'#ffffff'} size={150} />
        </div>
      )}

      {!http.isLoading ? (
        friends.map((friend, index) => (
          <Card additionalStyles={styles.cardStyle} key={index}>
            <div className={styles.profileInfo}>Friends</div>
            <KeyValueTextBox name={'Name: '} value={friend.name} />
            <KeyValueTextBox name={'Surname: '} value={friend.surname} />
            <KeyValueTextBox name={'Email: '} value={friend.email} />
            <Button
              buttonHandler={() => {
                return removeFriendHandler(friend.email);
              }}
              additionalStyles={styles.btnStyle}
            >
              Remove friend
            </Button>
          </Card>
        ))
      ) : (
        <div style={{ marginTop: '20px', alignSelf: 'center' }}>
          <ClipLoader loading={http.isLoading} color={'#ffffff'} size={150} />
        </div>
      )}
      {friends.length === 0 && !http.isLoading && (
        <div style={{ marginTop: '20px', alignSelf: 'center' }}>
          <Card>Ready to make some friends!</Card>
        </div>
      )}
    </div>
  );
};

export default Profile;
