import { useCallback, useEffect, useState, useContext } from 'react';
import swal from 'sweetalert';
import AuthContext from '../auth/store/auth-context';
import useHttp from '../hooks/use-http';
import Card from '../UI/Card';
import KeyValueTextBox from '../UI/KeyValueTextBox.module.css';

const AllUsers = () => {
  const http = useHttp();
  const [results, setResults] = useState([]);
	const authCtx = useContext(AuthContext)

  const fetchSuccess = (data) => {
    setResults(data.people);
  };

  const fetchFail = (errorMessage) => {
    swal('FAILED', errorMessage || 'Failed to load all the users!', 'warning');
  };

  let { sendRequest } = http;

  useEffect(() => {
    let requestConfig = {
      url: 'http://localhost:5000/api/person/get-all-people',
			headers: {Authorization: `Bearer ${authCtx.token}`}, 
      method: 'GET',
    };
    sendRequest(requestConfig, fetchSuccess, fetchFail);
  }, [sendRequest]);

  return (
    <div>
      {results.map((element) => (
        <Card>
          <KeyValueTextBox
            name={'Name: '}
            value={element.name}
          ></KeyValueTextBox>
          <KeyValueTextBox
            name={'Surname: '}
            value={element.surname}
          ></KeyValueTextBox>
          <KeyValueTextBox
            name={'Email: '}
            value={element.email}
          ></KeyValueTextBox>
        </Card>
      ))}
    </div>
  );
};

export default AllUsers;
