import { useReducer } from 'react';

const initialState = {
  value: '',
};

const validationReducer = (state, action) => {
  if (action.type === 'INPUT') {
    state.value = action.value;
		console.log(state.value)
  } else if (action.type === 'BLUR') {
  } else if (action.type === 'RESET') {
  }
};

const useInputValidation = (validation) => {
  const [inputState, inputValidation] = useReducer(validationReducer, initialState);
	console.log(inputState.value)
  const isValid = inputState.value === '' ? true : validation(inputState.value);

  const onChangeHandler = (event) => {
    inputValidation({ type: 'INPUT', value: event.target.value });
  };

  return { isValid, onChangeHandler };
};

export default useInputValidation;
