import { useEffect, useState, useContext, useCallback, useRef } from 'react';
import swal from 'sweetalert';
import ClipLoader from 'react-spinners/ClipLoader';

import AuthContext from '../auth/store/auth-context';
import useHttp from '../hooks/use-http';
import Button from '../UI/Button';
import Card from '../UI/Card';
import KeyValueTextBox from '../UI/KeyValueTextBox.js';
import styles from './AllUsers.module.css';
import { useHistory } from 'react-router-dom';
import Input from '../UI/Input';

const AllUsers = () => {
  const searchRefName = useRef();
  const searchRefSurname = useRef();
  const http = useHttp();
  const history = useHistory();
  const [results, setResults] = useState([]);
  const [selectState, setSelectState] = useState('ACQUAINTANCE');
  const authCtx = useContext(AuthContext);

  const fetchSuccess = (data) => {
    setResults(data.people);
  };

  const fetchFail = (errorMessage) => {
    swal('FAILED', errorMessage || 'Failed to load all the users!', 'warning');
  };

  let { sendRequest } = http;
  let { token, isLoggedIn } = authCtx;

  const fetchAllPeople = useCallback(() => {
    let requestConfig = {
      url: 'http://localhost:5000/api/person/get-non-friends',
      headers: { Authorization: `Bearer ${token}` },
      method: 'GET',
    };
    sendRequest(requestConfig, fetchSuccess, fetchFail);
  }, [sendRequest, token]);

  useEffect(() => {
    if (isLoggedIn) {
      fetchAllPeople();
    } else {
      history.replace('/login');
    }
  }, [fetchAllPeople, isLoggedIn, history]);

  const addFriendSuccess = (data) => {
    swal(
      'SUCCESS',
      data.message || 'Successfully added a new friend!',
      'success'
    );
    fetchAllPeople();
  };

  const addFriendFail = (errorMessage) => {
    swal('Oops', errorMessage || 'Failed to add a new friend!', 'warning');
  };

  const addFriendHandler = (email) => {
    let requestConfig = {
      url: `http://localhost:5000/api/person/add-friend?email=${encodeURI(
        email
      )}&type=${encodeURI(selectState)}`,
      headers: { Authorization: `Bearer ${token}` },
      method: 'POST',
    };
    sendRequest(requestConfig, addFriendSuccess, addFriendFail);
  };

  const onChangeSelectHandler = (event) => {
    setSelectState(event.target.value);
    console.log(event.target.value);
  };

  const checkSearchParamsValidity = (params) => {
    if (params.trim().split(' ').length > 2) {
      swal(
        'WARNING',
        'You can only include name and surname as search parameters!',
        'warning'
      );

      return false;
    }

    return true;
  };

  const searchSuccess = (data) => {
    if (data.people.length === 0) {
      swal('Oops', 'No data was found!', 'warning');
      return;
    }
    setResults(data.people);
  };

  const searchFail = (errorMessage) => {
    swal('Oops', errorMessage || 'No data was found!', 'warning');
  };

  const searchHandler = () => {
    if (!checkSearchParamsValidity(searchRef.current.rawValue)) {
      return;
    }
    let name = searchRef.current.rawValue.trim().split(' ')[0];
    let surname = searchRef.current.rawValue.trim().split(' ')[1];

    let requestConfig = {
      url:
        'http://localhost:5000/api/person/search?' +
        `name=${encodeURI(name ?? '')}&surname=${encodeURI(surname ?? '')}`,
      headers: { Authorization: `Bearer ${token}` },
      method: 'GET',
    };
    sendRequest(requestConfig, searchSuccess, searchFail);
    searchRef.current.refValue.current.value = '';
  };

  return (
    <div className={styles.container}>
      {results.length !== 0 && (
        <div className={styles.searchDiv}>
          <Input
            ref={searchRef}
            label={''}
            additionalStylesLabel={styles.searchLabel}
            additionalStylesInput={styles.searchInput}
						placeholder={'Name'}
          ></Input>{' '}
          <Input
            ref={searchRef}
            label={''}
            additionalStylesLabel={styles.searchLabel}
            additionalStylesInput={styles.searchInput}
						placeholder={'Surname'}
          ></Input>
          <Button buttonHandler={searchHandler}>Search</Button>
        </div>
      )}
      {!http.isLoading ? (
        results.map((element, index) => (
          <Card additionalStyles={styles.cardStyle} key={index}>
            <KeyValueTextBox name={'Name: '} value={element.name} />
            <KeyValueTextBox name={'Surname: '} value={element.surname} />
            <KeyValueTextBox name={'Email: '} value={element.email} />
            <select
              name='type'
              id='type'
              className={styles.select}
              onChange={onChangeSelectHandler}
              value={selectState}
            >
              <option value='ACQUAINTANCE'>Acquaintance</option>
              <option value='FRIEND'>Friend</option>
              <option value='BEST_FRIEND'>Best friend</option>
              <option value='GOOD_FRIEND'>Good friend</option>
            </select>
            <Button
              buttonHandler={() => {
                return addFriendHandler(element.email);
              }}
              additionalStyles={styles.btnStyle}
            >
              Add friend
            </Button>
          </Card>
        ))
      ) : (
        <div style={{ marginTop: '100px' }}>
          <ClipLoader loading={http.isLoading} color={'#ffffff'} size={150} />
        </div>
      )}

      {results.length === 0 && !http.isLoading && (
        <div style={{ marginTop: '100px' }}>
          <Card>All users are already friends :)</Card>
        </div>
      )}
    </div>
  );
};

export default AllUsers;
