import { useCallback, useContext, useEffect, useState } from 'react';
import ClipLoader from 'react-spinners/ClipLoader';

import styles from './Requests.module.css';

import KeyValueTextBox from '../UI/KeyValueTextBox';
import Card from '../UI/Card';
import Button from '../UI/Button';
import useHttp from '../hooks/use-http';
import swal from 'sweetalert';
import AuthContext from '../auth/store/auth-context';

const Requests = () => {
  const http = useHttp();
  const authCtx = useContext(AuthContext);
  const [requests, setRequests] = useState();

  const { token } = authCtx;
  const { sendRequest, isLoading } = http;

  const getRequestsSuccess = (data) => {
    setRequests(data.people);
  };

  const getRequestsFail = () => {
    swal('Oops', 'No requests pending!', 'info');
  };

  const getRequestsHandler = useCallback(() => {
    let requestConfig = {
      url: 'http://localhost:5000/api/person/get-requests',
      headers: { Authorization: `Bearer ${token}` },
      method: 'GET',
    };
    sendRequest(requestConfig, getRequestsSuccess, getRequestsFail);
  }, [sendRequest, token]);

  useEffect(() => {
    getRequestsHandler();
  }, [getRequestsHandler]);

  const approveSuccess = () => {
    getRequestsHandler();
  };

  const approveFail = () => {
    swal('Oops', 'Failed to update request status!', 'error');
  };

  const approveHandler = useCallback(
    (email) => {
      let requestConfig = {
        url: `http://localhost:5000/api/person/approve-request?email=${encodeURI(
          email
        )}&type=${encodeURI('USER')}`,
        headers: { Authorization: `Bearer ${token}` },
        method: 'PUT',
      };
      sendRequest(requestConfig, approveSuccess, approveFail);
    },
    [sendRequest, token, approveSuccess]
  );

  return (
    <div className={styles.container}>
      {!isLoading ? (
        requests.map((request, index) => (
          <Card additionalStyles={styles.cardStyle} key={index}>
            <KeyValueTextBox name={'Name: '} value={request.friend.name} />
            <KeyValueTextBox
              name={'Surname: '}
              value={request.friend.surname}
            />
            <KeyValueTextBox name={'Email: '} value={request.friend.email} />
            <KeyValueTextBox
              name={'Type: '}
              value={request.type.replace('_', ' ')}
            />
            <Button
              buttonHandler={() => {
                return approveHandler(request.friend.email);
              }}
              additionalStyles={styles.btnStyle}
            >
              Approve
            </Button>
          </Card>
        ))
      ) : (
        <div style={{ marginTop: '20px', alignSelf: 'center' }}>
          <ClipLoader loading={http.isLoading} color={'#ffffff'} size={150} />
        </div>
      )}
    </div>
  );
};

export default Requests;
