import { useReducer } from 'react';

const initialState = {
  value: '',
  hasError: false,
};

const validationReducer = (state, action) => {
  if (action.type === 'INPUT') {
    return { value: action.value, hasError: false };
  } else if (action.type === 'BLUR') {
		console.log(action.validate(state.value))
    return { ...state, hasError: action.validate(state.value) };
  } else if (action.type === 'RESET') {
  }
};

const useInputValidation = (validate) => {
  const [inputState, inputValidation] = useReducer(
    validationReducer,
    initialState
  );
  console.log(inputState.value);

  const onChangeHandler = (event) => {
    inputValidation({ type: 'INPUT', value: event.target.value });
  };

  const onBlurHandler = (event) => {
    inputValidation({ type: 'BLUR', validate });
  };

  return { hasError: inputState.hasError, onChangeHandler, onBlurHandler };
};

export default useInputValidation;
